/**
 * Interfaces with AWS- Dynamo Db for storing skill specific data, session etc.
 */

var AWS = require('aws-sdk');
var docClient = new AWS.DynamoDB.DocumentClient();
AWS.config.update(
    {
        region:"us-east-1",
    }
);

exports.read=function readSessionID(sessionId, callback) {
    AWS.config.update(
        {
            'region':'us-east-1',
        }
    );
    var params = {
        TableName: "reader",
        Key: {
            "sessionid": sessionId
        }
    };
    docClient.get(params, function (err, data) {
        if (err) {
            callback(false, err);
            console.log("Unable to read item. JSON:", JSON.stringify(err, null, 2));

        }
        else {
            console.log(data);
            if (Object.keys(data).length == 0) {
                callback(false);
            }
            else {
                callback(data);
            }

        }
    });
}

exports.create=function createSession(sessionID,value, callback) {
    var params = {
        TableName: "reader",
        Item: {
            "sessionid": sessionID,
            "value": value
        }
    };
    docClient.put(params, function (err, data) {
        if (err) {
            console.log("Error create session entry: " + err);
        }
        else {
            console.log("Added Item:", JSON.stringify(data, null, 2));
            callback(true);
        }
    });
}
